mapper.update:
UPDATE RENT_BOOK
SET RENT_BOOK_CHECK = 0
WHERE BOOK_NO = #{bookNo}
AND MEMBER_NO = #{memberNo}

UPDATE BOOK_INFO
SET AVALABLE_COUNT = AVALABLE_COUNT - 1
WHERE BOOK_NO = #{bookNo}

UPDATE LOAN_BOOK
SET LOAN_BOOK_STATUS = 0
WHERE LOAN_BOOK_NO = #{loanBookNo}

UPDATE BOOK_INFO
SET AVALABLE_COUNT = AVALABLE_COUNT + 1
WHERE BOOK_NO = #{bookNo}

UPDATE NEW_BOOK
SET NEW_BOOK_CHECK = 1
WHERE NEW_BOOK_NO = #{newBookNo}
